handlePipelineStepErrors   ( stepName:   'executeHealthCheck' ,   stepParameters:   parameters )   { 
   def   url   =   new   Utils (). getMandatoryParameter ( parameters ,   'url' ,   null ) 
   def   statusCode   =   curl ( url ) 
   if   ( statusCode   !=   '200' ) 
     error   "Health Check failed: ${statusCode}"  }
